import React, { Component } from 'react'
import {Button, InputGroup, FormControl} from 'react-bootstrap'

export default class AddProject extends Component {
    state={
        images : [1],
        imagesUrl :[]
    }
    onChange = (e) => {

    if (e.target.value.length > 0) {
      this.setState({
        [e.target.name]: e.target.value,
        [e.target.name + "lable"]: false
      })

    } else {
      this.setState({
        [e.target.name]: e.target.value,
        [e.target.name + "lable"]: true
      })
    }
    console.log(this.state);

  }
addImageUrl = (e) =>{
    this.state.imagesUrl.push(e.target.value)
}


    render() {
        return (
            <div>
                  <Button onClick={()=>this.props.addProject(false)} variant="danger">Cancel X</Button>
<br /> 
<InputGroup className="mb-3">
    <InputGroup.Prepend>
      <InputGroup.Text id="inputGroup-sizing-default">Title</InputGroup.Text>
    </InputGroup.Prepend>
    <FormControl
    onChange={this.onChange} 
    name="title"
      aria-label="Default"
      aria-describedby="inputGroup-sizing-default"
    />
  </InputGroup>
    {/* map this VV */}
    {this.state.images.map(url => <>
    <img src={url} />
    <InputGroup size="sm" className="mb-3">
    <InputGroup.Prepend>
      <InputGroup.Text onChange={this.addImageUrl} id="inputGroup-sizing-sm">Image</InputGroup.Text>
    </InputGroup.Prepend>
    <FormControl aria-label="Small" aria-describedby="inputGroup-sizing-sm" />
  </InputGroup>
</>
        )}
<InputGroup>
    <InputGroup.Prepend>
      <InputGroup.Text>Description</InputGroup.Text>
    </InputGroup.Prepend>
    <FormControl onChange={this.onChange} name="description" as="textarea" aria-label="With textarea" />
  </InputGroup>

  <br />
            </div>
        )
    }
}
